installation.script:
#!/bin/bash

apt update
apt install -y curl jq wget

mkdir -p /mnt/server/
cd /mnt/server/

if [[ -z $_INSTALL_MINECRAFT_VERSION || ${_INSTALL_MINECRAFT_VERSION,,} == "latest" ]]; then
  echo -e "Getting minecraft latest version";
  _INSTALL_MINECRAFT_VERSION=$(curl -sSL https://meta.fabricmc.net/v2/versions/game | jq -r '.[] | select(.stable == true )|.version' | head -n1)
elif [[ ${_INSTALL_MINECRAFT_VERSION,,} == "snapshot" ]]; then
  echo -e "Getting minecraft snapshot version";
  _INSTALL_MINECRAFT_VERSION=$(curl -sSL https://meta.fabricmc.net/v2/versions/game | jq -r '.[] | select(.stable == false )|.version' | head -n1)
else
  if [[ -z $(curl -sSL https://meta.fabricmc.net/v2/versions/game | jq -r ".[] | select(.version == \"${_INSTALL_MINECRAFT_VERSION}\" )|.version") ]]; then
    echo "Minecraft version $_INSTALL_MINECRAFT_VERSION not found";
    exit 1;
  fi
fi

if [[ -z $_INSTALL_FABRIC_VERSION || ${_INSTALL_FABRIC_VERSION,,} == "latest" ]]; then
  echo -e "Getting fabric latest version";
  _INSTALL_FABRIC_VERSION=$(curl -sSL https://meta.fabricmc.net/v2/versions/installer | jq -r '.[] | select(.stable == true )|.version' | head -n1)
elif [[ ${_INSTALL_FABRIC_VERSION,,} == "snapshot" ]]; then
  echo -e "Getting fabric snapshot version";
  _INSTALL_FABRIC_VERSION=$(curl -sSL https://meta.fabricmc.net/v2/versions/installer | jq -r '.[] | select(.stable == false )|.version' | head -n1)
else
  if [[ -z $(curl -sSL https://meta.fabricmc.net/v2/versions/installer | jq -r ".[] | select(.version == \"${_INSTALL_FABRIC_VERSION}\" )|.version") ]]; then
    echo "Fabric version $_INSTALL_FABRIC_VERSION not found";
    exit 1;
  fi
fi

if [[ -z $_INSTALL_FABRIC_LOADER_VERSION || ${_INSTALL_FABRIC_LOADER_VERSION,,} == "latest" ]]; then
  echo -e "Getting fabric loader latest version";
  _INSTALL_FABRIC_LOADER_VERSION=$(curl -sSL https://meta.fabricmc.net/v2/versions/loader | jq -r '.[] | select(.stable == true )|.version' | head -n1)
elif [[ ${_INSTALL_FABRIC_LOADER_VERSION,,} == "snapshot" ]]; then
  echo -e "Getting fabric loader snapshot version";
  _INSTALL_FABRIC_LOADER_VERSION=$(curl -sSL https://meta.fabricmc.net/v2/versions/loader | jq -r '.[] | select(.stable == false )|.version' | head -n1)
else
  if [[ -z $(curl -sSL https://meta.fabricmc.net/v2/versions/loader | jq -r ".[] | select(.version == \"${_INSTALL_FABRIC_LOADER_VERSION}\" )|.version") ]]; then
    echo "Fabric loader version $_INSTALL_FABRIC_LOADER_VERSION not found";
    exit 1;
  fi
fi

echo -e "Downloading fabric";
wget -O fabric-installer.jar https://maven.fabricmc.net/net/fabricmc/fabric-installer/$_INSTALL_FABRIC_VERSION/fabric-installer-$_INSTALL_FABRIC_VERSION.jar

echo -e "Installing fabric";
java -jar fabric-installer.jar server -mcversion $_INSTALL_MINECRAFT_VERSION -loader $_INSTALL_FABRIC_LOADER_VERSION -downloadMinecraft

echo -e "Removing installer"
rm "fabric-installer.jar" || true

echo -e "Agree eula";
echo "eula=true" > "eula.txt";

echo -e "Install Complete"
