#!/bin/bash
# Forge Installation Script
#
# Server Files: /mnt/server
apt update
apt install -y curl jq

if [[ ! -d /mnt/server ]]; then
  mkdir /mnt/server
fi

cd /mnt/server

PROMOTIONS=$(curl -sSL https://files.minecraftforge.net/maven/net/minecraftforge/forge/promotions_slim.json)

echo -e "Getting minecraft version"
if [[ -z ${_INSTALL_MINECRAFT_VERSION} || ${_INSTALL_MINECRAFT_VERSION,,} == "latest" ]]; then
 _INSTALL_MINECRAFT_VERSION=$(echo $PROMOTIONS | jq -r '.promos | to_entries | last | .key');
 _INSTALL_MINECRAFT_VERSION=${_INSTALL_MINECRAFT_VERSION%%-*}
else
 FIND_VERSION=$(echo $PROMOTIONS | jq -r ".promos | keys | .[] | select(. | startswith(\"${_INSTALL_MINECRAFT_VERSION}\"))" | head -1);
 if [[ -z $FIND_VERSION ]]; then
    echo "Minecraft version ${_INSTALL_MINECRAFT_VERSION} not found";
    exit 1;
  fi
  _INSTALL_MINECRAFT_VERSION=${FIND_VERSION%%-*}
fi

echo -e "Getting forge version"
if [[ -z ${_INSTALL_FORGE_VERSION} || ${_INSTALL_FORGE_VERSION,,} == "latest" ]]; then
 _INSTALL_FORGE_VERSION=$(echo $PROMOTIONS | jq -r ".promos | to_entries | .[] | select(.key | startswith(\"${_INSTALL_MINECRAFT_VERSION}\")) | .value" | head -1);
else
 FIND_VERSION=$(echo $PROMOTIONS | jq -r ".promos | to_entries | .[] | select(.value == \"${_INSTALL_FORGE_VERSION}\") | .value" | head -1);
 if [[ -z ${_INSTALL_FORGE_VERSION} ]]; then
    echo "Forge version ${_INSTALL_FORGE_VERSION} not found";
    exit 1;
  fi
  _INSTALL_FORGE_VERSION=$FIND_VERSION
fi


DOWNLOAD_LINK=https://maven.minecraftforge.net/net/minecraftforge/forge/${_INSTALL_MINECRAFT_VERSION}-${_INSTALL_FORGE_VERSION}/forge-${_INSTALL_MINECRAFT_VERSION}-${_INSTALL_FORGE_VERSION}-installer.jar

if [[ -n $(curl -X HEAD -I $DOWNLOAD_LINK | head -n 1 | grep 404) ]]; then
  echo "Installer version ${_INSTALL_MINECRAFT_VERSION}-${_INSTALL_FORGE_VERSION} not found";
  exit 1;
fi

echo -e "Downloading forge installer"
curl -sSL -o installer.jar $DOWNLOAD_LINK

echo -e "Removing old jars"
rm -r ./forge*.jar || true

echo -e "Installing forge"
java -jar installer.jar --installServer

echo -e "Removing installer"
rm installer.jar installer.jar.log || true

echo -e "Agree eula";
echo "eula=true" > "eula.txt";

echo -e "Install Complete"
